async function a(n){const e=await fetch(n);if(!e.ok)throw new Error("ðŸ’© Could not fetch posts");return await e.json()}async function w(n){const e=await a(n),s=4,i=80,u=e.filter(t=>!t.draft).map(t=>{const f=t.description.length>i?"...":"",d=t.description.substring(0,i)+f;return{...t,description:d}});function o(){return[...u].sort((t,r)=>new Date(r.date).getTime()-new Date(t.date).getTime())}function c(){return o().slice(0,s)}function p(){return o().sort((t,r)=>r.views-t.views).slice(0,s)}function g(){return o().filter(t=>t.series).slice(0,s)}function l(){return o().sort(()=>Math.random()-.5).slice(0,s)}return{posts:o(),latestPost:c()[0],latest:c(),popular:p(),series:g(),picks:l()}}async function h(n){const e=await a(n);return[...new Set(e.map(s=>s.category))]}export{w as a,h as g};
